package leetcode;

import java.util.Stack;

public class FindTheDifference {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="";
		String t="";
		System.out.println(stringsDifference(s,t));

	}

    public static char stringsDifference(String s, String t) {
    	int len = t.length();
    	Stack<Character> stack = new Stack<Character>();
    	int[] hashT = new int[256];
    	for(int i=0;i<len;i++){
    		if(i<s.length()){
    			char tokenS = s.charAt(i);
    			hashT[tokenS]--;
    		}
    		char tokenT = t.charAt(i);
    		hashT[tokenT]++;
    		if(hashT[tokenT]>0){
    			//difference = tokenT;
    			stack.push(tokenT);
    			//System.out.println(stack.peek());
    		}
    		while(!stack.isEmpty() && hashT[stack.peek()]<1){
    			//System.out.println(stack.peek()+"  "+hashT[stack.peek()]);
    			stack.pop();
    		}
    		
    	}

    	return stack.peek();
        
    }
    
    
}
